$svg_path = AIPOSTS_ASSET_PATH . 'images' . DIRECTORY_SEPARATOR . 'svg_icons';
$files = scandir($svg_path);
$files = array_values(array_diff($files, array('.', '..')));
$tree = [];
foreach ($files as $item) {
	if (file_exists($svg_path . DIRECTORY_SEPARATOR . $item)) {
		$name = explode('.', $item);
		$tree[array_shift($name)] = $item;
	}
}

return $tree;

